mapper.select:
SELECT food_classify.`food_id`AS 'food_id' ,food_classify.`food_category` AS food_category ,food_classify.`food_code` food_code,food_classify.`food_redundancy` AS  food_redundancy
FROM food_classify WHERE 1 = 1

    and food_id=#{foodId}


    and foodcategory=#{foodCategory}


    and food_code=#{foodCode}


    and food_redundancy=#{foodRedundancy}

mapper.update:
UPDATE food_classify
food_category = #{foodCategory}, food_code = #{foodCode}, food_redundancy = #{foodRedundancy},
where food_id=#{foodId}
