mapper.update:
UPDATE food_monicker
classify_id = #{classifyId}, food_name = #{foodName}, food_picture = #{foodPicture}, food_describe = #{foodDescribe}, food_redundancy = #{foodRedundancy},
where food_id = #{foodId}

mapper.select:
SELECT food_monicker.`food_id` AS foodId,food_monicker.`classify_id` AS classifyId,
       food_monicker.`food_name` AS foodName,food_monicker.`food_picture` AS foodPicture,
       food_monicker.`food_describe` AS foodDescribe, food_monicker.`food_redundancy` AS foodRedundancy FROM food_monicker WHERE classify_id =#{number}
