mapper.select:
SELECT id, purchaseDate, foodNameId, foodCatalog, unit, num, unitPrice, budgetary, purchaseNum, purchasePrice, purchaseCost, grossProfit, vendor, purchaser, note, create_time, update_time
FROM purchase_records p
WHERE 1 = 1

    and foodNameId = #{ foodNameId }


    and purchaseDate = #{ purchaseDate }

mapper.update:
UPDATE purchase_records
purchaseDate = #{purchaseDate}, foodNameId = #{foodNameId}, foodCatalog = #{foodCatalog}, unit = #{unit}, num = #{num}, unitPrice = #{unitPrice},
budgetary = #{budgetary}, purchaseNum = #{purchaseNum}, purchasePrice = #{purchasePrice}, purchaseCost = #{purchaseCost}, grossProfit = #{grossProfit}, vendor = #{vendor},
purchaser = #{purchaser}, note = #{note}, update_time = #{update_time}
where id=#{id}
